AOS.init();var typed=new Typed("#typed",{strings:[" <p class='about__upper-text'>Привет, Меня зовут Андрей.</p> <p class='gold-text about__main-text'>Я — FullStack разработчик.</p> <p class='about__lower-text'>На этом сайте можно ознакомиться с моими <a class='gold-text' href='#'>работами</a> и <a class='gold-text' href='#contacts'>связаться</a> со мной.</p> <div class = 'button-wrapper'> <a class='about__button btn' href = '#contacts'>Написать мне</a> <a class='about__button btn' href = 'img/cv.pdf' download>Скачать резюме</a></div>"],typeSpeed:35});document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});const burger=document.querySelector(".hamburger"),nav=document.querySelector(".header-links"),navLinks=document.querySelectorAll(".header-links__item"),html=document.querySelector("html"),checkbox=document.querySelector(".checkbox"),blackout=document.querySelector(".blackout");function showNav(){nav.classList.add("active"),html.classList.add("active"),blackout.classList.add("active")}function hideNav(){nav.classList.remove("active"),html.classList.remove("active"),blackout.classList.remove("active")}burger.addEventListener("click",()=>{0==checkbox.checked?showNav():hideNav()}),navLinks.forEach(e=>{e.addEventListener("click",()=>{checkbox.checked=!1,hideNav()})});const form=document.querySelector(".formData"),modal=document.querySelector(".modal");form.addEventListener("submit",e=>{e.preventDefault(),modal.classList.toggle("active"),html.classList.toggle("active"),form.reset()});const modalClose=document.querySelector(".modal__close");modalClose.addEventListener("click",()=>{modal.classList.toggle("active"),html.classList.remove("active")});